import React, { Fragment, useState, useEffect } from 'react';
import EditTodo from './EditTodo';

const ListAllTodos = () => {
    const[todos, setTodos] = useState([]);

    //delete todo
    async function deleteTodo (id) {
        try {
            const res = await (`http://localhost:5000/${id}`, {
                method:DELETE
            });
            setTodos(todo.filter(todo.tid !== id))
        } catch (error) {
            console.error(error);
        }
    }



    //get all todos
    async function getTodos () {
        try {
            res = await fetch("http://localhost:5000/todos");
            const todoArray = await res.json();
            setTodos(todoArray);
        } catch (error) {
            console.error(error);
        }
    }

    useEffect(() => {
        getTodos();
    }, []);

    return (
        <Fragment>
            <table class="table mt-5 text-center">
                <thead>
                    <tr>
                        <th>Description</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {/* <tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
                </tbody>
            </table>

        </Fragment>
    )
}

export default ListAllTodos;
